        fun getFirstStageToken(): String {
            val url = "$BASE$ENDPOINT/anchor?$PARAMS"
            val request = Request.Builder()
                .url(url)
                .header("Content-Type", CONTENT_TYPE)
                .addHeader("User-Agent", "Mozilla/5.0 (Linux; Android 11; Pixel 3a) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/90.0.4430.210 Mobile Safari/537.36")
                .build()
            val response = cirnoClient.newCall(request).execute()
            val body = response.body?.string()
            val pattern = Regex(firstStagePattern)
            val result = pattern.find(body!!)
            return result!!.groupValues[1]
        }

        fun getSecondStageToken(firstStageToken: String): String {
            val url = "$BASE$ENDPOINT/reload?k=$K"
            val postData = getPostData(firstStageToken)
            val request = Request.Builder()
                .url(url)
                .header("Content-Type", CONTENT_TYPE)
                .addHeader("User-Agent", "Mozilla/5.0 (Linux; Android 11; Pixel 3a) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/90.0.4430.210 Mobile Safari/537.36")
                .post(postData.toRequestBody())
                .build()
            val response = cirnoClient.newCall(request).execute()
            val body = response.body?.string()
            val pattern = Regex(secondStagePattern)
            val result = pattern.find(body!!)
            return result!!.groupValues[1]
        }
        fun getRecaptchaToken(): String {
            val firstStageToken = getFirstStageToken()
            return getSecondStageToken(firstStageToken)
        }
